class Team extends BaseTeam {
	
	
	public static function getTeamsWithoutPoolForDivision($divisionId) {
		$q = Doctrine_Query::create()
				->from('Team t')
				->leftJoin("t.PoolTeam pt")
				->where('t.division_id = ? AND pt.pool_id is null', $divisionId);
		$teams = $q->execute();
		return $teams;
	}

}